import axios from 'axios';

const BASE_API = 'http://localhost:3001/api';

const getPaymentMethods = async (url) => {
    const resp = await axios.get('http://localhost:3001/api/paymentMethods');
    return resp.data;
};

const makePayment = async () => {
    const resp = await axios.post('http://localhost:3001/api/initiatePayment');
    return resp.data;
};

const makeDetailsCall = async () => {
    const resp = await axios.post('http://localhost:3001/api/additionalDetails');
    return resp.data;
};


export { getPaymentMethods, makePayment, makeDetailsCall };
