import './App.css'
import { useEffect, useState } from 'react';
import getConfig from './services/utils/config';
import CheckOut from './components/Checkout';
import { makePayment, makeDetailsCall } from "./services/api";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  useHistory
} from "react-router-dom";

function App() {
  const [checkoutConfig, setCheckoutConfig] = useState(null);
  const [paymentResponse, setPaymentResponse] = useState(null);
  const history = useHistory();

  const onSubmit = (state, dropin) => {
    // Global configuration for onSubmit
    // Your function calling your server to make the `/payments` request
    makePayment(state.data)
      .then(response => {
        if (response.action) {
          // Drop-in handles the action object from the /payments response
          dropin.handleAction(response.action);
        } else {
          // Your function to show the final result to the shopper
          handlePaymentResponse(response);
        }
      })
      .catch(error => {
        throw Error(error);
      });
  };

  const handlePaymentResponse = (res, dropin) => {
    if (res.action) {
      dropin.handleAction(res.action);
    } else {
      setPaymentResponse(res);
      switch (res.resultCode) {
        case 'Authorised':
          history.push('/success');
          break;

        default:
          break;
      }
    }
  };

  const onAdditionalDetails = (state, dropin) => {
    makeDetailsCall(state.data)
      .then(response => {
        if (response.action) {
          // Drop-in handles the action object from the /payments response
          dropin.handleAction(response.action);
        } else {
          // Your function to show the final result to the shopper
          handlePaymentResponse(response, dropin);
        }
      })
      .catch(error => {
        throw Error(error);
      });
  };

  useEffect(() => {
    getConfig(onSubmit, onAdditionalDetails)
      .then(res => {
        setCheckoutConfig(res);
      });
  }, []);

  return (
    <div className="app-wrapper">
      <Router>
        <Switch>
          <Route path="/">
            {checkoutConfig ?
              <CheckOut
                config={checkoutConfig}
              /> :
              <></>
            }
          </Route>
          <Route path="/success">
            <div>
              <h1>Payment Successfull!</h1>
              <h2>Order #{paymentResponse.pspReference} has been placed.</h2>
            </div>
          </Route>
        </Switch>
      </Router>

    </div>
  );
}



export default App;
