import AdyenCheckout from '@adyen/adyen-web';
import '@adyen/adyen-web/dist/adyen.css';
import axios from 'axios';
import { useEffect } from 'react';

const getPaymentMethods = () => {
  axios.post('http://localhost:3001/paymentMethods', {
    countryCode: 'NL',
    shopperLocale: 'nl-NL',
    currency: 'EUR',
    value: 1000,
    channel: 'Web'
  })
};

const makePayment = () => {

};

const showFinalResult = () => {

};

const makeDetailsCall = () => {

};


function App() {
  const configuration = {
    paymentMethodsResponse: getPaymentMethods, // The `/paymentMethods` response from the server.
    clientKey: process.env.ADYEN_APIKEY, // Web Drop-in versions before 3.10.1 use originKey instead of clientKey.
    locale: "en-US",
    environment: "test",
    onSubmit: (state, dropin) => {
      // Global configuration for onSubmit
      // Your function calling your server to make the `/payments` request
      makePayment(state.data)
        .then(response => {
          if (response.action) {
            // Drop-in handles the action object from the /payments response
            dropin.handleAction(response.action);
          } else {
            // Your function to show the final result to the shopper
            showFinalResult(response);
          }
        })
        .catch(error => {
          throw Error(error);
        });
    },
    onAdditionalDetails: (state, dropin) => {
      // Your function calling your server to make a `/payments/details` request
      makeDetailsCall(state.data)
        .then(response => {
          if (response.action) {
            // Drop-in handles the action object from the /payments response
            dropin.handleAction(response.action);
          } else {
            // Your function to show the final result to the shopper
            showFinalResult(response);
          }
        })
        .catch(error => {
          throw Error(error);
        });
    },
    paymentMethodsConfiguration: {
      card: { // Example optional configuration for Cards
        hasHolderName: true,
        holderNameRequired: true,
        enableStoreDetails: true,
        hideCVC: false, // Change this to true to hide the CVC field for stored cards
        name: 'Credit or debit card',
        onSubmit: () => { }, // onSubmit configuration for card payments. Overrides the global configuration.
      }
    }
  };

  useEffect(() => {
    const checkout = new AdyenCheckout(configuration);

    const dropin = checkout
        .create('dropin', {
        // Starting from version 4.0.0, Drop-in configuration only accepts props related to itself and cannot contain generic configuration like the onSubmit event.
            openFirstPaymentMethod:false
        })
      .mount('#dropin-container');
  })


  return (
    <div id="dropin-container">

    </div>
  );
}

export default App;
