import { getPaymentMethods } from "../api";

const getConfig = async (onSubmit, onAdditionalDetails) => {
    const paymentMethods = await getPaymentMethods();
    return {
      paymentMethodsResponse: paymentMethods, // The `/paymentMethods` response from the server.
      clientKey: process.env.REACT_APP_ADYEN_CLIENT_KEY, // Web Drop-in versions before 3.10.1 use originKey instead of clientKey.
      locale: "en_US",
      environment: "test",
      onSubmit,
      onAdditionalDetails,
      paymentMethodsConfiguration: {
        card: {
          hasHolderName: true,
          holderNameRequired: false,
          enableStoreDetails: true,
          hideCVC: false, // Change this to true to hide the CVC field for stored cards
          name: 'Credit or debit card',
          onSubmit, // onSubmit configuration for card payments. Overrides the global configuration.
        }
      }
    };
  }

  export default getConfig;
