import AdyenCheckout from '@adyen/adyen-web';
import '@adyen/adyen-web/dist/adyen.css';
import { useEffect, useRef, useState } from 'react';
import getConfig from './services/utils/config';
import { getPaymentMethods } from './services/api';

const paymentMethodsMock = {
  groups: [
      {
          name: "Credit Card",
          types: [
              "mc",
              "visa",
              "amex",
              "maestro",
              "amex_applepay",
              "cup",
              "diners",
              "discover",
              "jcb",
              "mc_applepay"
          ]
      }
  ],
  paymentMethods: [
      {
          details: [
              {
                  key: "number",
                  type: "text"
              },
              {
                  key: "expiryMonth",
                  type: "text"
              },
              {
                  key: "expiryYear",
                  type: "text"
              },
              {
                  key: "cvc",
                  type: "text"
              },
              {
                  key: "holderName",
                  optional: true,
                  type: "text"
              }
          ],
          name: "Credit Card",
          type: "scheme"
      },
      {
          details: [
              {
                  items: [
                      {
                          id: "1121",
                          name: "Test Issuer"
                      },
                      {
                          id: "1154",
                          name: "Test Issuer 5"
                      },
                      {
                          id: "1153",
                          name: "Test Issuer 4"
                      },
                      {
                          id: "1152",
                          name: "Test Issuer 3"
                      },
                      {
                          id: "1151",
                          name: "Test Issuer 2"
                      },
                      {
                          id: "1162",
                          name: "Test Issuer Cancelled"
                      },
                      {
                          id: "1161",
                          name: "Test Issuer Pending"
                      },
                      {
                          id: "1160",
                          name: "Test Issuer Refused"
                      },
                      {
                          id: "1159",
                          name: "Test Issuer 10"
                      },
                      {
                          id: "1158",
                          name: "Test Issuer 9"
                      },
                      {
                          id: "1157",
                          name: "Test Issuer 8"
                      },
                      {
                          id: "1156",
                          name: "Test Issuer 7"
                      },
                      {
                          id: "1155",
                          name: "Test Issuer 6"
                      }
                  ],
                  key: "issuer",
                  type: "select"
              }
          ],
          name: "iDEAL",
          supportsRecurring: true,
          type: "ideal"
      },
      {
          name: "PayPal",
          supportsRecurring: true,
          type: "paypal"
      },
      {
          details: [
              {
                  key: "sepa.ownerName",
                  type: "text"
              },
              {
                  key: "sepa.ibanNumber",
                  type: "text"
              }
          ],
          name: "SEPA Direct Debit",
          supportsRecurring: true,
          type: "sepadirectdebit"
      },
      {
          name: "Paysafecard",
          supportsRecurring: true,
          type: "paysafecard"
      },
      {
          name: "Generic GiftCard",
          supportsRecurring: false,
          type: "genericgiftcard"
      },
      {
          name: "UnionPay",
          supportsRecurring: true,
          type: "unionpay"
      }
  ]
};

function App() {
  const [checkoutConfig, setCheckoutConfig] = useState(null);

  useEffect(() => {
    getPaymentMethods().then(res => {
      const configuration = getConfig(paymentMethodsMock);
      setCheckoutConfig(configuration);
    })

  }, []);


  return (
    <CheckOut config={checkoutConfig} />
  );
}

const CheckOut = ({config}) => {
  const dropinRef = useRef();

  useEffect(() => {
    console.log({config})
    const checkout = new AdyenCheckout(config);
    checkout
      .create('dropin', {
        // Starting from version 4.0.0, Drop-in configuration only accepts props related to itself and cannot contain generic configuration like the onSubmit event.
        openFirstPaymentMethod: false
      })
      .mount(dropinRef.current);

  }, [config, dropinRef]);

  return (
    <div ref={dropinRef} id="dropin-container">
    </div>
  );
}



export default App;
