import AdyenCheckout from '@adyen/adyen-web';
import '@adyen/adyen-web/dist/adyen.css';
import { useEffect, useRef, useState } from 'react';
import getConfig from './services/utils/config';
import { getPaymentMethods } from './services/utils/handlers';

function App() {
  const configuration = getConfig();
  const [checkoutConfig, setCheckoutConfig] = useState(configuration || null);

  const updateConfigPaymentMethods = (paymentMethods) => {
    configuration.paymentMethodsResponse = paymentMethods;
  }

  useEffect(() => {
    getPaymentMethods().then(res => {
      updateConfigPaymentMethods(res);
      setCheckoutConfig(configuration);
    })

  }, []);


  return (
    <CheckOut config={checkoutConfig} />
  );
}

const CheckOut = ({config}) => {
  const dropinRef = useRef();

  useEffect(() => {
    console.log({config})
    const checkout = new AdyenCheckout(config);
    checkout
      .create('dropin', {
        // Starting from version 4.0.0, Drop-in configuration only accepts props related to itself and cannot contain generic configuration like the onSubmit event.
        openFirstPaymentMethod: false
      })
      .mount(dropinRef.current);

  }, [config, dropinRef]);

  return (
    <div ref={dropinRef} id="dropin-container">
    </div>
  );
}



export default App;
